@article{zhou_etal2013Ultradeepsequencing,
  title = {Ultra-Deep Sequencing Enables High-Fidelity Recovery of Biodiversity for Bulk Arthropod Samples without {{PCR}} Amplification},
  author = {Zhou, Xin and Li, Yiyuan and Liu, Shanlin and Yang, Qing and Su, Xu and Zhou, Lili and Tang, Min and Fu, Ribei and Li, Jiguang and Huang, Quanfei},
  year = {2013},
  month = dec,
  journal = {GigaScience},
  volume = {2},
  number = {1},
  pages = {4},
  issn = {2047-217X},
  doi = {10.1186/2047-217X-2-4},
  url = {https://academic.oup.com/gigascience/article-lookup/doi/10.1186/2047-217X-2-4},
  urldate = {2024-11-01},
  abstract = {Background Next-generation-sequencing (NGS) technologies combined with a classic DNA barcoding approach have enabled fast and credible measurement for biodiversity of mixed environmental samples. However, the PCR amplification involved in nearly all existing NGS protocols inevitably introduces taxonomic biases. In the present study, we developed new Illumina pipelines without PCR amplifications to analyze terrestrial arthropod communities. Results Mitochondrial enrichment directly followed by Illumina shotgun sequencing, at an ultra-high sequence volume, enabled the recovery of Cytochrome c Oxidase subunit 1 (COI) barcode sequences, which allowed for the estimation of species composition at high fidelity for a terrestrial insect community. With 15.5 Gbp Illumina data, approximately 97\% and 92\% were detected out of the 37 input Operational Taxonomic Units (OTUs), whether the reference barcode library was used or not, respectively, while only 1 novel OTU was found for the latter. Additionally, relatively strong correlation between the sequencing volume and the total biomass was observed for species from the bulk sample, suggesting a potential solution to reveal relative abundance. Conclusions The ability of the new Illumina PCR-free pipeline for DNA metabarcoding to detect small arthropod specimens and its tendency to avoid most, if not all, false positives suggests its great potential in biodiversity-related surveillance, such as in biomonitoring programs. However, further improvement for mitochondrial enrichment is likely needed for the application of the new pipeline in analyzing arthropod communities at higher diversity.},
  copyright = {http://www.springer.com/tdm},
  langid = {english},
  file = {/Users/airvine/Zotero/storage/IYVPRKPL/Zhou et al. - 2013 - Ultra-deep sequencing enables high-fidelity recove.pdf}
}

